import React, { Component } from 'react';
import { connect } from 'react-redux';
import {
  Button,
  Col,
  Form,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Row
} from 'reactstrap';
import axios from 'axios';

class createEvent extends Component {
    constructor(props){
        super(props)
        this.state = {
            eventName: '',
            vendorName: '',
            proposedDate: [],
            confirmedDate: '',
            proposedLocation: '',
            status: '', // 1 for approve, 0 for reject, 2 for pending
            reason: '',
        }
    }

    render(){
        return(
        <Form className="LoginForm">
            <h1>Create Event Form</h1>
            <p className="text-muted">Fill out this form about your event details</p>
            <InputGroup className="mb-3">
              <InputGroupAddon addonType="prepend">
                <InputGroupText>
                  <i className="icon-user" />
                </InputGroupText>
              </InputGroupAddon>
              <Input
                type="text"
                placeholder="EventName"
                autoComplete="eventname"
                value={this.state.eventName}
                onChange={val => {
                  this.setState({
                    eventName: val.target.value
                  });
                }}
              />
            </InputGroup>
            <InputGroup className="mb-3">
              <InputGroupAddon addonType="prepend">
                <InputGroupText>
                  <i className="icon-user" />
                </InputGroupText>
              </InputGroupAddon>
              <Input
                type="text"
                placeholder="Vendor Name"
                autoComplete="Vendor Name"
                value={this.state.vendorName}
                onChange={val => {
                  this.setState({
                    vendorName: val.target.value
                  });
                }}
              />
            </InputGroup>
            <InputGroup className="mb-3">
              <InputGroupAddon addonType="prepend">
                <InputGroupText>
                  <i className="icon-user" />
                </InputGroupText>
              </InputGroupAddon>
              <Input
                type="text"
                id="datePick"
                placeholder="Proposed Date"
                autoComplete="Proposed Date"
                value={this.state.proposedDate}
                onChange={val => {
                  this.setState({
                    proposedDate: val.target.value
                  });
                }}
              />
            </InputGroup>
            <InputGroup className="mb-4">
              <InputGroupAddon addonType="prepend">
                <InputGroupText>
                  <i className="icon-lock" />
                </InputGroupText>
              </InputGroupAddon>
              <Input
                type={this.state.types}
                placeholder="Password"
                autoComplete="current-password"
                value={this.state.password}
                onChange={val => {
                  this.setState({
                    password: val.target.value
                  });
                }}
              />
              <InputGroupAddon addonType="append">
                  <Button 
                    outline color="primary"
                    onClick={this.showHide}>
                     {this.state.types === 'password' ? <i class="fa fa-eye"></i>  : <i class="fa fa-eye-slash"></i>}
                  </Button>
              </InputGroupAddon>
            </InputGroup>
            <Row>
              <Col xs="6">
                <Button
                  color="primary"
                  className="px-4 text-white"
                  type="submit"
                  onClick={this.check_login}
                >
                  Login
                </Button>
              </Col>
              {/* <Col xs="6" className="text-right">
                        <Button color="link" className="px-0">Forgot password?</Button>
                    </Col> */}
            </Row>
        </Form>
        )
    }
}

export default createEvent
