import React, { Component } from 'react';
import { connect } from 'react-redux';
import Datepicker from 'react-multiple-datepicker'
import {
  Button,
  Col,
  Form,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Row
} from 'reactstrap';
import axios from 'axios';

class createEvent extends Component {
    constructor(props){
        super(props)
        this.state = {
            eventName: '',
            vendorName: '',
            proposedDate: [],
            proposedLocation: '',
        }
    }

    render(){
        console.log(this.state)
        return(
            <Form className="LoginForm">
                <h1>Create Event Form</h1>
                <p className="text-muted">Fill out this form about your event details</p>
                <InputGroup className="mb-3">
                <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                    <i className="icon-user" />
                    </InputGroupText>
                </InputGroupAddon>
                <Input
                    type="text"
                    placeholder="Event Name"
                    autoComplete="eventname"
                    value={this.state.eventName}
                    onChange={val => {
                    this.setState({
                        eventName: val.target.value
                    });
                    }}
                />
                </InputGroup>
                <InputGroup className="mb-3">
                <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                    <i className="icon-user" />
                    </InputGroupText>
                </InputGroupAddon>
                <Input
                    type="text"
                    placeholder="Vendor Name"
                    autoComplete="Vendor Name"
                    value={this.state.vendorName}
                    onChange={val => {
                    this.setState({
                        vendorName: val.target.value
                    });
                    }}
                />
                </InputGroup>
                <InputGroup className="mb-3">
                <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                    <i className="icon-user" />
                    </InputGroupText>
                </InputGroupAddon>
                <Datepicker 
                    onSubmit={dates => this.setState({
                        proposedDate: dates
                    })}
                />
                </InputGroup>
                <InputGroup className="mb-3">
                <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                    <i className="icon-user" />
                    </InputGroupText>
                </InputGroupAddon>
                <Input
                    type="text"
                    placeholder="Proposed Location"
                    autoComplete="proposedlocation"
                    value={this.state.proposedLocation}
                    onChange={val => {
                    this.setState({
                        proposedLocation: val.target.value
                    });
                    }}
                />
                </InputGroup>
                <Row>
                <Col xs="6">
                    <Button
                    color="primary"
                    className="px-4 text-white"
                    type="submit"
                    onClick={console.log(this.state)}
                    >
                    submit
                    </Button>
                </Col>
                {/* <Col xs="6" className="text-right">
                            <Button color="link" className="px-0">Forgot password?</Button>
                        </Col> */}
                </Row>
            </Form>
        )
    }
}

export default createEvent
