import React, { Component } from 'react';
import { connect } from 'react-redux';
import {
  Button,
  Col,
  Form,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Row,
  Table
} from 'reactstrap';
import jwtSimple from 'jwt-simple'
import axios from 'axios';
import { API_SERVER, READ_EVENT } from '../../api'
import { toast } from 'react-toastify';

class Event extends Component {
  constructor(props){
    super(props)
    this.credential = jwtSimple.decode(localStorage.getItem('token'), "SECRET")
    this.state = {
      adminType: this.credential.adminType, // toget admin type account vendor/hr admin
      accountId: this.credential._id,
      eventDetails: []
    }
  }

  // toget all event by account type vendor/hr admin
  getAllEvent(e){
    e.preventDevault()
    axios
      .get(API_SERVER + READ_EVENT + `?adminType=${this.state.adminType}&accountId${this.state.accountId}`, {
        headers: {
          Authorization: localStorage.getItem('token')
        }
      }).then(response => {
        var result = response.data
        if(!result.error) {
          this.setState({
            eventDetails: result.data
          })
          toast.success(result.message)
        } else {
          toast.error(result.message)
        }
      }).catch(error => {
        toast.error(error)
      })
  }



  render(){
    console.log(this.state.eventDetails)
    return(
      <Table striped>
      <thead>
        <tr>
          <th>#</th>
          <th>Company Name</th>
          <th>Vendor Name</th>
          <th>Proposed Date</th>
          <th>Status</th>
          <th>Date Created</th>
          <th>Action</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <th scope="row">1</th>
          <td>Healthsys</td>
          <td>Otto</td>
          <td>02/09/2019</td>
          <td>Pending</td>
          <td>02/02/2019</td>
          <td><Button color="success">View</Button></td>
        </tr>
      </tbody>
    </Table>
    )
  }
}

export default Event
