import React, { Component } from 'react';
import { connect } from 'react-redux';
import Datepicker from 'react-multiple-datepicker'
import {
  Button,
  Col,
  Form,
  Input,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Row
} from 'reactstrap';
import axios from 'axios';
import { API_SERVER, CREATE_EVENT, ALL_USERS  } from '../../api'
import { toast } from 'react-toastify'
import jwtSimple from 'jwt-simple'
import { actionGetHRList } from '../../actions'

class _createEvent extends Component {
    constructor(props){
        super(props)
        this.submitEvent = this.submitEvent.bind(this)
        this.getAllVendor = this.getAllVendor.bind(this)
        this.state = {
            eventName: '',
            vendorId: '',
            companyId: jwtSimple.decode(localStorage.getItem('token'), "SECRET")._id,
            proposedDate: [],
            proposedLocation: '',
        }
        this.adminType = jwtSimple.decode(localStorage.getItem('token'), "SECRET").adminType
    }

    componentDidMount(){
      this.getAllVendor()
    }

    submitEvent(e){
      e.preventDefault()
      axios
        .post(API_SERVER + CREATE_EVENT, this.state, {
          headers: {
            Authorization: localStorage.getItem('token')
          }
        }).then(response => {
          var result = response.data
          console.log(result)
          if (!result.error) {
            window.location.href = '/event'
            toast.success(result.message)
            console.log(result)
          } else {
            alert.error(result.error)
          }
        }).catch(error => {
          alert.error(error)
        })
    }

    getAllVendor(){
      axios
        .get(API_SERVER + ALL_USERS + `?${this.adminType}`, {
          headers: {
            Authorization: localStorage.getItem('token')
          }
        }).then(response => {
          var result = response.data
          if (!result.error) {
            console.log(result)
            this.props.getVendorList(result)
          }
        }).catch(error => {

        })
    }

    render(){
        console.log(this.state)
        return(
            <Form className="LoginForm">
                <h1>Create Event Form</h1>
                <p className="text-muted">Fill out this form about your event details</p>
                <InputGroup className="mb-3">
                <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                    <i className="icon-user" />
                    </InputGroupText>
                </InputGroupAddon>
                <Input
                    type="text"
                    placeholder="Event Name"
                    autoComplete="eventname"
                    value={this.state.eventName}
                    onChange={val => {
                    this.setState({
                        eventName: val.target.value
                    });
                    }}
                />
                </InputGroup>
                <InputGroup className="mb-3">
                <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                    <i className="icon-user" />
                    </InputGroupText>
                </InputGroupAddon>
                <Input
                    type="select"
                    placeholder="Vendor Name"
                    autoComplete="Vendor Name"
                    onClick={val => {
                    this.setState({
                        vendorId: val.target.data
                    });
                    }}
                >
                {
                  this.props.vendors.map((vendors, key) => (
                    <option key={key} data={vendors._id} value={vendors.name}>{vendors.name}</option>
                  ))
                }
                </Input>
                </InputGroup>
                <InputGroup className="mb-3">
                <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                    <i className="icon-user" />
                    </InputGroupText>
                </InputGroupAddon>
                <Datepicker 
                    onSubmit={dates => this.setState({
                        proposedDate: dates
                    })}
                />
                </InputGroup>
                <InputGroup className="mb-3">
                <InputGroupAddon addonType="prepend">
                    <InputGroupText>
                    <i className="icon-user" />
                    </InputGroupText>
                </InputGroupAddon>
                <Input
                    type="text"
                    placeholder="Proposed Location"
                    autoComplete="proposedlocation"
                    value={this.state.proposedLocation}
                    onChange={val => {
                    this.setState({
                        proposedLocation: val.target.value
                    });
                    }}
                />
                </InputGroup>
                <Row>
                <Col xs="6">
                    <Button
                    color="primary"
                    className="px-4 text-white"
                    type="submit"
                    onClick={this.submitEvent}
                    >
                    submit
                    </Button>
                </Col>
                {/* <Col xs="6" className="text-right">
                            <Button color="link" className="px-0">Forgot password?</Button>
                        </Col> */}
                </Row>
            </Form>
        )
    }
}

const mapStateToProps = (state, ownProps) => ({
  vendors: state.user.vendor
})

const mapDispatchToProps = (dispatch, ownProps) => ({
  getVendorList: data => dispatch(actionGetHRList(data))
})

export const createEvent = connect(mapStateToProps, mapDispatchToProps)(_createEvent)
export default createEvent
